import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  titulo:string = 'Contador';
  numero:number=10;
}
<h1>{{titulo}}</h1>

<button (click)="numero=numero+1;"></button>
<span>{{numero}}</span>
<button (click)="numero=numero-1"></button>
