import { Component } from '@angular/core';

@Component({
  selector: 'app-listado',
  templateUrl: './listado.component.html',
  styleUrls: ['./listado.component.css']
})
export class ListadoComponent {
   heroes:string[]=['SPIDERMAN','IROMAN','HULK','THOR','CAPITAN AMERICA'];
   heroesBorrados:string='';
   borrar():string{
      //this.heroes.splice(0,3);//ELIMINAR UN RANGO
      const heroesBorrados= this.heroes.shift();//ELIMINA EL PRIMER REGISTRO Y NOS RETORNA
      console.log(heroesBorrados);
      return heroesBorrados?.toString();
    }
  }
<p>LISTADO HEROES</p>


<ul>
  <li *ngFor="let heroe of heroes;let i=index">
     {{i}} {{heroe}}
  </li>
</ul>
<div>
  <h3>HEROE BORRADO</h3>
  <span>{{heroesBorrados}}</span>
</div>
<button (click)="borrar()">Borrar</button>
