import { Component } from "@angular/core";

@Component({
  selector:'app-heroe',
  templateUrl:'./heroe.component.html'
})
export class HeroeComponent{
  nombre:string='IROMAN';
  edad:number=48;
  get nombreCapitalizado(){
    return this.nombre.toUpperCase();
  }
  mostrar():string{
    return `${this.nombre} - ${this.edad}`;
  }
}
<dl>
  <td>Nombre:</td>
  <dd>{{nombre}}</dd>
  <td>Edad:</td>
  <dd>{{nombre}}</dd>
  <td>Función:</td>
  <dd>{{mostrar()}}</dd>
  <td>Capitalizado:</td>
  <dd>{{nombre}}</dd>
</dl>
