import { Component } from "@angular/core";

@Component({
  selector:'app-contador',
  template:`
  <h1>{{titulo}}</h1>
<h3>La base es: <strong>{{base}}</strong></h3>
<button (click)="acumular(base)">+{{base}}</button>
<span>{{numero}}</span>
<button (click)="acumular(-base)">-{{base}}</button>

  `
})


export class ContadorModule{
  titulo:string = 'Contador';
  numero:number=10;
  base:number=5;

  acumular(signo:number){
    this.numero+=signo;
  }

}
