import { Component } from "@angular/core";

@Component({
  selector:'app-heroe',
  templateUrl:'./heroe.component.html'
})
export class HeroeComponent{
  nombre:string='IROMAN';
  edad:number=48;
  get nombreCapitalizado():string{
    return this.nombre.toLowerCase();
  }
  mostrar():string{
    return `${this.nombre} - ${this.edad}`;
  }
  cambiarNombre():void{
     this.nombre='Spider- Man';
  }
  cambiarEdad():void{
    this.edad=30;
  }
}
<dl>
  <td>Nombre:</td>
  <dd>{{nombre}}</dd>
  <td>Edad:</td>
  <dd>{{edad}}</dd>
  <td>Función:</td>
  <dd>{{mostrar()}}</dd>
  <td>Capitalizado:</td>
  <dd>{{nombreCapitalizado}}</dd>
</dl>
<button (click)="cambiarNombre()">Cambiar Nombre</button>
<button>Cambiar Edad</button>
